use criterion::{black_box, criterion_group, criterion_main, Criterion};
use fmt::{count_bytes::CountBytes, count_chars::CountChars};

const COUNTER_1: &'static str = "counter";
const COUNTER_10: &'static str =
    "countercountercountercountercountercountercountercountercountercounter";
const COUNTER_100: &'static str = "";
const COUNTER_1000: &'static str = "";
const COUNTER_10000: &'static str = "";

pub fn count_bytes(c: &mut Criterion) {
    let mut c = c.benchmark_group("count_bytes");
    c.bench_function("counter", |b| {
        b.iter(|| CountBytes::count(black_box(COUNTER_1)))
    });
    c.bench_function("counter10", |b| {
        b.iter(|| CountBytes::count(black_box(COUNTER_10)))
    });
    c.bench_function("counter100", |b| {
        b.iter(|| CountBytes::count(black_box(COUNTER_100)))
    });
    c.bench_function("counter1000", |b| {
        b.iter(|| CountBytes::count(black_box(COUNTER_1000)))
    });
    c.bench_function("counter10000", |b| {
        b.iter(|| CountBytes::count(black_box(COUNTER_10000)))
    });
}

pub fn count_chars(c: &mut Criterion) {
    let mut c = c.benchmark_group("count_chars");
    c.bench_function("counter", |b| {
        b.iter(|| CountChars::count(black_box(COUNTER_1)))
    });
    c.bench_function("counter10", |b| {
        b.iter(|| CountChars::count(black_box(COUNTER_10)))
    });
    c.bench_function("counter100", |b| {
        b.iter(|| CountChars::count(black_box(COUNTER_100)))
    });
    c.bench_function("counter1000", |b| {
        b.iter(|| CountChars::count(black_box(COUNTER_1000)))
    });
    c.bench_function("counter10000", |b| {
        b.iter(|| CountChars::count(black_box(COUNTER_10000)))
    });
}

criterion_group!(benches, count_bytes, count_chars);
criterion_main!(benches);
